package com.tns.day1;

public class FirstProgram {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=100;
		int b=90;
		//arithmetic
		System.out.println("a+b="+(a+b));
		System.out.println("a-b="+(a-b));
		System.out.println("a/b="+(a/b));
		System.out.println("a*b="+(a*b));
		System.out.println("a%b="+(a%b));
		//increment 
		System.out.println("a++="+a);
		//decrement
		System.out.println("a--="+a);
		
		//relational 
		System.out.println("a<b="+(a<b));
		System.out.println("a>=b="+(a>=b));
		System.out.println("a<=b="+(a<=b));
		System.out.println("a<b="+(a<b));
	    System.out.println("a==b="+(a==b));
	    //assignment
		System.out.println("a=b="+(a=b));
		
		System.out.println("a+=b="+(a+=b));
		System.out.println("a-=b="+(a-=b));
		System.out.println("a*=b="+(a*=b));
		System.out.println("a/=b="+(a/=b));
		System.out.println("a%=b="+(a%=b));
		//ternary
		System.out.println((a>b)?"hi":"hello");


	}

}
